<?phpclass Onlines{    private $Page;    private $Players;    public function __construct($Page)    {        $this->Page = $Page;        $this->Players = array();        $Query = mysql_query("SELECT id FROM accounts WHERE playing = 1");        if (!mysql_num_rows($Query)) {            return;        }        while ($Values = mysql_fetch_assoc($Query)) {            array_push($this->Players, $Values['id']);        }    }    public function LoadPlayers()    {        $NewPlayers = array();        $this->Page->GetFunctions()->Load('player');        foreach ($this->Players as $Player) {            array_push($NewPlayers, new Player($this->Page, $Player));        }        $this->Players = $NewPlayers;    }    public function GetPlayers()    {        return $this->Players;    }}?>